/* QUESTION
 *       2)  Suppose a String s is initialized by 
       String s = "a friendly face";  
                a. Compute the value of the expression 
         s.charAt(2); 
                b. What is the value of 
         s.length()? 
                c. What is the value of 
         s.substring(2,9)? 
         s.substring(4)?
 */
public class StringMethodTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "a friendly face";
		System.out.println("Output of s.charAt(2);: "+s.charAt(2));
		System.out.println("Output of s.length(): "+s.length());
		System.out.println("Output of s.substring(2,9): "+s.substring(2,9));
		System.out.println("Output of s.substring(4): "+s.substring(4));
	}

}
